import { Box, Image, Text, VStack } from "@chakra-ui/react";
import ProfileCard from "./ProfileCard";
import { ProfileContainer } from "./Common";
import { QUERY_PROJECTS, QUERY_USER } from "../../utils/queries";
import { useQuery } from "@apollo/client";
import { ProjectAuthor } from "./Common";

const Profile = (props) => {
  const { data: userData } = useQuery(QUERY_USER, {
    variables: { username: AuthService.getUsername() },
  });

  const user = userData?.user.username || {};

  const { loading, data } = useQuery(QUERY_PROJECTS);
  const projects = data?.projects || [];
  return (
    <ProfileContainer>

        <VStack
          p={7}
          m="auto"
          width="fit-content"
          borderRadius={6}
          bg="gray.700"
        >
          <Image
            borderRadius="full"
            boxSize="80px"
            src="https://bit.ly/kent-c-dodds"
            alt="Profile"
          />
          <Text>
            <ProjectAuthor>ivsir</ProjectAuthor>
          </Text>
          <Text fontSize="lg" color="gray.400">
            Software Engineer
          </Text>
        </VStack>
      <ProfileCard />
    </ProfileContainer>
  );
};
export default Profile;
