import { useToast } from '@chakra-ui/react';
import { useState } from 'react';
import axiosClient from '../config/axios';
import AuthService from "../utils/auth";
import { QUERY_USER } from "../utils/queries";
import { useQuery } from "@apollo/client";

const useMutation = ({ url, method = 'POST' }) => {
  const toast = useToast();
  const [state, setState] = useState({
    isLoading: false,
    error: '',
  });

  const fn = async data => {
    setState(prev => ({
      ...prev,
      isLoading: true,
    }));

    // Create a unique folder name for each user (replace 'userId' with the actual user ID)
    const userId = 'replace-with-actual-user-id'; // You should get the user ID from your authentication system

    // Add a folder creation step before making the HTTP request
    axiosClient({ url: `/create-folder/${userId}`, method: 'POST' })
      .then(() => {
        // Now, you can proceed with your original HTTP request
        axiosClient({ url, method, data })
          .then(() => {
            setState({ isLoading: false, error: '' });
            toast({
              title: 'Successfully Added Image',
              status: 'success',
              duration: 2000,
              position: 'top',
            });
          })
          .catch(error => {
            setState({ isLoading: false, error: error.message });
          });
      })
      .catch(error => {
        setState({ isLoading: false, error: error.message });
      });
  };

  return { mutate: fn, ...state };
};


