import { useState, useContext } from "react";
import { Link } from "react-router-dom";

import { useMutation } from "@apollo/client";
import { ADD_USER, ADD_BUCKET } from "../../utils/mutations";

import AuthService from "../../utils/auth";

import { createS3Bucket } from "../../utils/aws"; // Import your S3 library

import { AccountContext } from "../../components/accountBox/AccountContext";

import {
  BoldLink,
  BoxContainer,
  LoginContainer,
  FormContainer,
  InputContainer,
  Input,
  MutedLink,
  ButtonContainer,
  SubmitButton,
} from "../accountBox/Common";

export function SignupForm() {
  const [formState, setFormState] = useState({
    username: "",
    email: "",
    password: "",
  });

  const [addUser] = useMutation(ADD_USER);
  const [addBucket] = useMutation(ADD_BUCKET);

  const handleChange = (event) => {
    const { name, value } = event.target;
    console.log(name, value, formState);
    setFormState({
      ...formState,
      [name]: value,
    });
  };

  const handleFormSubmit = async (event) => {
    event.preventDefault();
    try {
      const { data } = await addUser({
        variables: { ...formState },
      });

      const userId = data.addUser._id; // Assuming your response has the user's ID
      const bucketName = await createS3Bucket(userId);

      await addBucket({
        variables: {
          userId,
          bucketName,
        },
      });

      AuthService.login(data.addUser.token);
    } catch (e) {
      console.error(e);
    }
  };

  //
  const { switchToSignin } = useContext(AccountContext);

  return (
    <BoxContainer>
      <LoginContainer id="signup-container" onSubmit={handleFormSubmit}>
        <InputContainer>
          <Input
            id="signup-input1"
            type="text"
            placeholder="Username"
            name="username"
            value={formState.username}
            onChange={handleChange}
          />
        </InputContainer>
        <InputContainer>
          <Input
            id="signup-input2"
            type="email"
            placeholder="Email"
            name="email"
            value={formState.email}
            onChange={handleChange}
          />
        </InputContainer>
        <InputContainer>
          <Input
            id="signup-input3"
            type="password"
            placeholder="Password"
            name="password"
            value={formState.password}
            onChange={handleChange}
          />
        </InputContainer>
        <ButtonContainer>
          <SubmitButton id="signup-button" type="submit">
            Sign Up
          </SubmitButton>
        </ButtonContainer>
        <MutedLink id="muted-link" href="#">
          Already have an account?
          <BoldLink id="bold" href="#" onClick={switchToSignin}>
            Sign In{" "}
          </BoldLink>
        </MutedLink>
      </LoginContainer>
    </BoxContainer>
  );
}
